import React from 'react';
class Body extends React.Component {
	constructor(){
		super();
		this.state = {
		input : null,
		component : null,
		}
	}
	handleInput =(event)=>{
		this.setState({
			input: event.target.value
		})
	}
	handleClick =()=>{
		this.setState({
		component : this.state.input
		})
	}

	render(){
		return(
			<div>
			<input type={"text"} onChange={this.handleInput}/>
			<button onClick={this.handleClick}/>
			</div>
			)
	}
}
export default Body;
