import React from 'react';
import team1 from './images/team1.jpg';
import './Home.css'
class Home extends React.Component {
	constructor(){
		super();
		this.state = {
			team1Hover: false
		}
	}
	handleMouseEnter=()=>{
		this.setState({
			team1Hover:true
		})
	}
	handleMouseLeave= ()=>{
		this.setState({
			team1Hover: false
		})
	}

	render(){
		return(
			<div className="home">	
				<div className="welcome">
					<h1>Welcome to Team Up</h1>
					<div onMouseEnter={this.handleMouseEnter}
                    onMouseLeave={this.handleMouseLeave}>
						<img src={team1} style={{
							width: "100%",
							height: "auto",
							zIndex:2,
							opacity: 1,
							marginBottom:"1vw"
						}} className={"team1"}/>
						<GetStarted hover={this.state.team1Hover} />
					</div>
				</div>
			</div>
			)
	}
}
const GetStarted=({team1Hover})=>{
	if(team1Hover === true){
		return  <div>whoa bro
				</div>
	}
}
export default Home;
